mapper.select:
select * from comment
	where co_bo_num = #{bo_num}
	order by co_ori_num desc, co_num asc
	limit #{cri.pageStart}, #{cri.perPageNum}

select * from comment
	where co_bo_num = #{comment.co_bo_num} and
		co_num = #{comment.co_num}

mapper.delete:
delete from comment
	where co_bo_num = #{comment.co_bo_num} and
		co_num = #{comment.co_num}
